import React, { useEffect, useState } from "react";
import {
  Image,
  ScrollView,
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
} from "react-native";


import api from "../../plugins/api";

export default function Fornecedores() {
  const [fornecedores, setFornecedores] = useState([]);

  useEffect(() => {
    async function carregarFornecedores() {
      const response = await api.get("fornecedores");
      setFornecedores(response.data);
    }
    carregarFornecedores();
  }, []);

  return (
    <View style={styles.container}>
      <View>
        <Text style={styles.titulo}>Fornecedores Parceiras</Text>
      </View>
      <ScrollView style={styles.lista}>
        {fornecedores.map((fornecedor) => (
          <TouchableOpacity style={styles.item} key={fornecedor.id}>
            <Image source={{ uri: fornecedor.image }} style={styles.imagem} />
            <View style={styles.info}>
              <Text style={styles.fornecedorTitulo}> {fornecedor.title} </Text>
              <View style={styles.descricao}>
                <MaterialIcons name="star" size={20} color="#FF7C01" />
                <Text style={styles.estrela}>{fornecedor.star || "Novo!"}</Text>
                <Text style={styles.categorias}> {fornecedor.categories}</Text>
                <Text style={styles.distancia}> {fornecedor.distance}</Text>
              </View>
              <Text style={styles.atraso}> {fornecedor.delay} </Text>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginVertical: 20,
    marginLeft: 20,
    marginRight: 0,
  },
  titulo: {
    fontSize: 23,
    fontWeight: "bold",
  },
  lista: {
    marginTop: 10,
  },
  item: {
    flexDirection: "row",
    padding: 20,
    borderWidth: 1,
    borderStyle: "solid",
    borderColor: "#1E90FF",
    borderRadius: 4,
    marginTop: 5,
    marginRight: 15,
  },
  imagem: {
    width: 60,
    height: 60,
    borderRadius: 30,
  },
  info: {
    marginLeft: 15,
  },
  restauranteTitulo: {
    fontWeight: "bold",
    color: "#333",
  },
  descricao: {
    flexDirection: "row",
    marginTop: 3,
    alignItems: "center",
  },
  estrela: {
    fontSize: 14,
    color: "#999",
  },
  categorias: {
    fontSize: 14,
    color: "#999",
  },
  distancia: {
    fontSize: 14,
    color: "#999",
  },
  atraso: {
    marginTop: 15,
    fontSize: 14,
    color: "#999",
  },
});
